CREATE DATABASE FUTEBOL;

USE FUTEBOL;

CREATE TABLE TIMES(
	NOME VARCHAR(50),
	SIGLA VARCHAR(10),
	ANO INTEGER,
	CORES VARCHAR(50),
	QNTD_JOGADORES INTEGER,
	TECNICO VARCHAR(50),
	PRESIDENTE VARCHAR(50),
	PATROCINADOR VARCHAR(50),
	FORNECEDOR VARCHAR(50),
	TITULOS INTEGER,
	MASCOTE VARCHAR(50),
	ESTADIO VARCHAR(50),
	CONTINENTE VARCHAR(50),
	PAIS VARCHAR(50),
	CIDADE VARCHAR(50),
	LIGA VARCHAR(50)
);


